func ShowHiddenLogo() {
	logoRow1 := " .-. .-.  .--.  .-. .-.,---.    .--.   .---.  _____           "
	logoRow2 := " | | | | / /\\ \\ |  \\| || .-.\\  / /\\ \\ / .-. )/___  / |\\    /| "
	logoRow3 := " | `-' |/ /__\\ \\|   | || |-' )/ /__\\ \\| | |(_)  / /) |(\\  / | "
	logoRow4 := " | .-. ||  __  || |\\  || |--' |  __  || | | |  / /(_)(_)\\/  | "
	logoRow5 := " | | |)|| |  |)|| | |)|| |    | |  |)|\\ `-' / / /___ | \\  / | "
	logoRow6 := " /(  (_)|_|  (_)/(  (_)/(     |_|  (_) )---' (_____/ | |\\/| | "
	logoRow7 := "(__)           (__)   (__)            (_)            '-'  '-' "

	logo := []string{logoRow1, logoRow2, logoRow3, logoRow4, logoRow5, logoRow6, logoRow7}
	fmt.Print("\033[H\033[2J")
	SlowFadeIn(logo, 10*time.Millisecond)
	time.Sleep(50 * time.Millisecond)
	SlowFadeOut(logo, 10*time.Millisecond)

	figData, err := base64.StdEncoding.DecodeString(figBase64)
	if err != nil {
		return
	}
	figDataStr := string(figData)
	// figDataLines := strings.Split(figDataStr, "\n")
	fmt.Println(figDataStr)
	time.Sleep(50 * time.Millisecond)
	fmt.Print("\033[H\033[2J")
}

func SlowFadeIn(logo []string, delay time.Duration) {
	for _, row := range logo {
		fmt.Println(row)
		time.Sleep(delay)
	}
}

func SlowFadeOut(logo []string, delay time.Duration) {
	for _, _ = range logo {
		fmt.Print("\033[H\033[2J")
		logo = logo[:len(logo)-1]
		for _, row := range logo {
			fmt.Println(row)
		}
		time.Sleep(delay)
	}
	fmt.Print("\033[H\033[2J")
}
